mapper.select:
	SELECT queidx, id,subject,content,reg_date,ansstatus, rnum
	FROM (
		SELECT queidx, id,subject,content,reg_date, ansstatus, type,
		ROW_NUMBER() OVER(ORDER BY queidx DESC) AS rnum FROM question WHERE id=#{param3}
	)
WHERE rnum BETWEEN #{param1} AND #{param2}

SELECT  * FROM( 
	SELECT ROW_NUMBER() OVER(ORDER BY b.bookidx DESC) AS rnum,b.bookIdx,b.bookstate "bstate",b.bookname,r.bookstate "rstate",r.id,r.reg_date,r.reservebookidx  FROM book b FULL OUTER JOIN reserveBook r ON b.bookidx = r.bookidx WHERE id=#{param3}
)WHERE rnum BETWEEN #{param1} AND #{param2}

SELECT  * FROM( 
	SELECT  ROW_NUMBER() OVER(ORDER BY hopebooksNumber DESC) AS rnum ,hopebooksnumber,id,hb_state,hb_date,hb_bookname,writer,publisher,hopereason FROM HopeBook WHERE id=#{param3}
)WHERE rnum BETWEEN #{param1} AND #{param2}
